import './App.css';
import React, { useState } from 'react';
import Navbar from './components/Navbar';
import { GiHamburgerMenu } from "react-icons/gi";
import Homeunion from './page/Homefarmer';
import User from './page/User';
import Board from './page/Board';
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";


function Appunion() {
  const [showNav, setShowNav] = useState(false);
  
  const toggleNav = () => {
    setShowNav(!showNav);
  };

  return (
    <div className="Appunion">
      <Router>
        <div style={{backgroundColor:"#024e9f" , textAlign:"center",height:"fit-content",fontSize:"28px",padding:"10px", fontWeight: 'bold'}}>P'daily</div>
        <header>
          <GiHamburgerMenu onClick={toggleNav} style={{ cursor: "pointer" ,color:"#024e9f"}}/>
          <div className='goright'></div>
        </header>
        <Navbar show={showNav} />
        <div className='main'>
          <Routes>
            <Route path="/" element={<User />} />
            <Route path="/user" element={<User />} />
            <Route path="/board" element={<Board />} />
            <Route path="/homeunion" element={<Homeunion />} />
          </Routes>
        </div>
      </Router>
    </div>
  );
}

export default Appunion;
