import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './Homepage.css'; // Import your own CSS file for styling

const divStyle = {
  color: '#082459',
  fontSize: '24px',
  fontFamily: 'Roboto',
  fontWeight: 700,
  wordWrap: 'break-word'
};

const Homepage = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };

  return (
    <div>
      <div style={{divStyle}}>ข่าวสารประจำวัน</div>
    <div className="slider-container">
      <Slider {...settings}>
        <a href="https://www.thebangkokinsight.com/news/business/937327/#google_vignette" target="_blank" rel="noopener noreferrer">
          <img src="cow1.jpg" alt="Cow 1" className="slick-image" />
        </a>
        <img src="cow2.jpg" alt="Cow 2" className="slick-image" />
        <img src="cow3.jpg" alt="Cow 3" className="slick-image" />
      </Slider>
    </div>
    </div>
  );
};

export default Homepage;
