import React, { useState } from 'react';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

const divStyle = {
  color: '#082459',
  fontSize: '24px',
  fontFamily: 'Roboto',
  fontWeight: 700,
  wordWrap: 'break-word',
  display: 'flex',
  justifyContent: 'center'
};

const boxget = {
  borderRadius: '10px',
};

const Inputfood = () => {
  const [inputValue, setInputValue] = useState('');

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleSave = (close) => {
    // Do something with the inputValue (e.g., save to state, send to server, etc.)
    console.log('Input Value:', inputValue);
    close();
  };

  return (
    <div>
      <Popup trigger={<button> กรอก </button>} modal nested>
        {(close) => (
          <div style={{ ...boxget, borderRadius: '10px' }}>
            <div style={divStyle}>
              กรอกอาหาร
            </div>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
              <input
                type="text"
                value={inputValue}
                onChange={handleInputChange}
                placeholder="Enter value..."
                style={{ margin: '10px' }}
              />
              <button onClick={() => handleSave(close)}>
                บันทึก
              </button>
            </div>
          </div>
        )}
      </Popup>
    </div>
  );
};

export default Inputfood;
