import React, { useState, useEffect } from 'react';
import './Slider.css'; // Import your styling for the slider

const images = [
  'image1.jpg',
  'image2.jpg',
  'image3.jpg',
  // Add more image URLs as needed
];

const Slider = () => {
  const [currentImage, setCurrentImage] = useState(0);

  const nextSlide = () => {
    setCurrentImage((prevImage) => (prevImage === images.length - 1 ? 0 : prevImage + 1));
  };

  const prevSlide = () => {
    setCurrentImage((prevImage) => (prevImage === 0 ? images.length - 1 : prevImage - 1));
  };

  useEffect(() => {
    const intervalId = setInterval(nextSlide, 5000); // Change 5000 to the desired interval in milliseconds

    return () => clearInterval(intervalId); // Cleanup function to clear the interval on component unmount
  }, []); // Empty dependency array to run the effect only once when the component mounts

  return (
    <div className="slider-container">
      <button onClick={prevSlide}>Previous</button>
      <div className="slider">
        {images.map((imageUrl, index) => (
          <div key={index} className={`slide ${index === currentImage ? 'active' : ''}`}>
            <img src={imageUrl} alt={`Slide ${index + 1}`} />
          </div>
        ))}
      </div>
      <button onClick={nextSlide}>Next</button>
    </div>
  );
};

export default SlidingPic;
